// Types for game content
export interface ValuesQuestion {
    id: string;
    phase: string;
    text: string;
    hint: string;
    points?: string[];
}

export interface DeepQuestion {
    id: string;
    text: string;
    hint: string;
}

export interface TruthOrDareItem {
    id: string;
    text: string;
    type: 'TRUTH' | 'DARE';
    hint: string;
}

export interface WouldYouRatherItem {
    id: string;
    optionA: string;
    optionB: string;
    hint: string;
}

export interface ComplimentItem {
    id: string;
    text: string;
    hint: string;
}

export interface RouletteItem {
    id: string;
    text: string;
    hint: string;
}

export interface MemoryItem {
    id: string;
    text: string;
    hint: string;
}

export type GameContentItem = ValuesQuestion | DeepQuestion | TruthOrDareItem | WouldYouRatherItem | ComplimentItem | RouletteItem | MemoryItem;

