// Helper function to get content for a specific mode
export function getGameContent(mode: string): GameContentItem[] {
    return sessionData[mode] || sessionData['values'];
}

// Helper to get truths and dares separately
export function getTruthsAndDares() {
    const items = sessionData['truth-or-dare'] as TruthOrDareItem[];
    return {
        truths: items.filter(item => item.type === 'TRUTH'),
        dares: items.filter(item => item.type === 'DARE')
    };
}
